function menu(){const e=document.querySelector(".menu__btn"),t=document.querySelector(".menu__list"),s=document.querySelectorAll(".menu__list-item"),n=document.querySelectorAll(".menu__list-auth");e.addEventListener("click",(()=>{e.classList.contains("menu__btn--active")?(e.classList.remove("menu__btn--active"),t.classList.remove("menu__list--active")):(e.classList.add("menu__btn--active"),t.classList.add("menu__list--active"))})),s.forEach((s=>{s.addEventListener("click",(()=>{e.classList.contains("menu__btn--active")&&(e.classList.remove("menu__btn--active"),t.classList.remove("menu__list--active"))}))})),n.forEach((s=>{s.addEventListener("click",(()=>{e.classList.contains("menu__btn--active")&&(e.classList.remove("menu__btn--active"),t.classList.remove("menu__list--active"))}))}))}menu();